            int id9 = await txn.rawInsert(
                'INSERT INTO varietyMal(id,name,details,link) VALUES(13, "്രേപ്പ് എസ്ക","\tമുന്തിരി മീസിൽസ്, എസ്ക, ബ്ലാക്ക് മീസിൽസ് അല്ലെങ്കിൽ സ്പാനിഷ് മീസിൽസ് എന്നും വിളിക്കപ്പെടുന്ന മുന്തിരിപ്പഴം, അതിന്റെ നിഗൂഢമായ രോഗലക്ഷണങ്ങൾ കൊണ്ട് മുന്തിരി കർഷകരെ വളരെക്കാലമായി ബാധിച്ചിട്ടുണ്ട്. ‘മീസിൽസ്’ എന്ന പേര് പഴത്തിൽ കാണപ്പെടുന്ന ഉപരിപ്ലവമായ പാടുകളെ സൂചിപ്പിക്കുന്നു. സീസണിൽ, പാടുകൾ ചർമ്മത്തിന്റെ ഉപരിതലത്തിൽ കൂടിച്ചേർന്ന് സരസഫലങ്ങൾ കറുത്തതായി മാറുന്നു. വിളവെടുപ്പിന് ഏതാനും ദിവസങ്ങൾക്ക് മുമ്പും കായ്കൾക്കിടയിൽ എപ്പോൾ വേണമെങ്കിലും പുള്ളി ഉണ്ടാകാം. ഫലവൃക്ഷത്തിൽ ബാധിച്ച സരസഫലങ്ങൾ പാകമാകാതിരിക്കുകയും ചുരുങ്ങുകയും ഉണങ്ങുകയും ചെയ്യും. പുള്ളിക്ക് പുറമേ, സീസണിൽ പിന്നീട് ബാധിച്ച കായ്കൾക്ക് കടുത്ത രുചിയും ഉണ്ടായിരിക്കും.\nവർഷാവർഷം അണുബാധകൾ രൂക്ഷമാകുമ്പോൾ ഇലയുടെ ലക്ഷണങ്ങൾ ഒരു ‘കടുവ വര’യുടെ സ്വഭാവമാണ്. നേരിയ അണുബാധയ്ക്ക് ഇലയുടെ ലക്ഷണങ്ങൾ ഉണ്ടാകാം, അത് മറ്റ് രോഗങ്ങളുമായോ പോഷകാഹാരക്കുറവുമായോ ആശയക്കുഴപ്പത്തിലാക്കാം. വെളുത്ത ഇനങ്ങളിൽ ക്ലോറോസിസ്, നെക്രോസിസ് എന്നിവ കാണിക്കും, ചുവന്ന ഇനങ്ങളിൽ ചുവന്ന പ്രദേശങ്ങളും നെക്രോസിസും ഉണ്ടാകും. ചിനപ്പുപൊട്ടൽ, ഇലയുടെ നിറവ്യത്യാസം, കഠിനമായ കേസുകളിൽ പൂർണ്ണമായ ഇലപൊഴിയൽ എന്നിവ വസന്തത്തിന്റെ തുടക്കത്തിലെ ലക്ഷണങ്ങളിൽ ഉൾപ്പെടുന്നു.\n\nഎങ്ങനെ പരിഹരിക്കാം\n\n1. ഇന്നുവരെ, ഈ രോഗം നിയന്ത്രിക്കാൻ ഫലപ്രദമായ മാർഗമില്ല. രോഗം ബാധിച്ച കായകൾ, ഇലകൾ, തടി എന്നിവ നീക്കം ചെയ്ത് നശിപ്പിക്കുക. മുറിവ് സീലന്റ് (അക്രിലിക് പെയിന്റിലെ 5% ബോറിക് ആസിഡ്) അല്ലെങ്കിൽ അവശ്യ എണ്ണ അല്ലെങ്കിൽ അനുയോജ്യമായ കുമിൾനാശിനികൾ ഉപയോഗിച്ച് ഫംഗസ് അണുബാധ കുറയ്ക്കുന്നതിന് മുറിവുകൾ സംരക്ഷിക്കുക.","https://grapes.extension.org/grapevine-measles/")');
            print('inserted1: $id9');
            int id10 = await txn.rawInsert(
                'INSERT INTO varietyMal(id,name,details,link) VALUES(14, "മുന്തിരി ഇല വരൾച്ച","\tMycosphaerellaceae കുടുംബത്തിലെ ഒരു ഫംഗസാണ് ഇസാരിയോപ്സിസ്. ഇസരിയോപ്‌സിസ് ഇലപ്പുള്ളി എന്ന സസ്യരോഗം യഥാർത്ഥത്തിൽ സ്യൂഡോസെർകോസ്‌പോറ വിറ്റിസാണ്, മുമ്പ് ഐ വിറ്റിസ് എന്നറിയപ്പെട്ടിരുന്നു.\n\nഎങ്ങനെ പരിഹരിക്കാം\n\n1. സീസണിൽ മറ്റ് രോഗങ്ങൾക്ക് തളിക്കുന്ന കുമിൾനാശിനികൾ ഈ രോഗം കുറയ്ക്കാൻ സഹായിക്കും.","https://plantvillage.psu.edu/topics/grape/infos")');
            print('inserted1: $id10');
            int id11 = await txn.rawInsert(
                'INSERT INTO varietyMal(id,name,details,link) VALUES(16, "സിട്രസ് ഗ്രീനിംഗ്","\tസിട്രസ് ഗ്രീനിംഗ് ഡിസീസ് ഒരു വെക്റ്റർ വഴി പകരുന്ന രോഗകാരി മൂലമുണ്ടാകുന്ന സിട്രസ് രോഗമാണ്. മോട്ടൈൽ ബാക്ടീരിയ, Candidatus Liberibacter spp എന്നിവയാണ് രോഗകാരികൾ. ഏഷ്യൻ സിട്രസ് സൈലിഡ്, ഡയഫോറിന സിട്രി, ആഫ്രിക്കൻ സിട്രസ് സൈലിഡ്, ട്രിയോസ എറിട്രീ എന്നിവയിലൂടെയാണ് രോഗം പകരുന്നതും പകരുന്നതും. ഇത് ഗ്രാഫ്റ്റ് ട്രാൻസ്മിസിബിൾ ആണെന്നും തെളിയിക്കപ്പെട്ടിട്ടുണ്ട്. മൂന്ന് വ്യത്യസ്ത തരം HLB നിലവിൽ അറിയപ്പെടുന്നു: ചൂട്-സഹിഷ്ണുതയുള്ള ഏഷ്യൻ രൂപം, ചൂട് സെൻസിറ്റീവ് ആഫ്രിക്കൻ, അമേരിക്കൻ രൂപങ്ങൾ. 1929-ൽ ഈ രോഗം ആദ്യമായി വിവരിക്കപ്പെട്ടത് 1943-ൽ ചൈനയിലാണ്. ആഫ്രിക്കൻ വ്യതിയാനം ആദ്യമായി റിപ്പോർട്ട് ചെയ്യപ്പെട്ടത് 1947-ൽ ദക്ഷിണാഫ്രിക്കയിലാണ്, അത് ഇപ്പോഴും വ്യാപകമാണ്. ഒടുവിൽ, ഇത് യുണൈറ്റഡ് സ്റ്റേറ്റ്സിനെ ബാധിച്ചു, 2005-ൽ ഫ്ലോറിഡയിൽ എത്തി. മൂന്നു വർഷത്തിനുള്ളിൽ, മിക്ക സിട്രസ് ഫാമുകളിലേക്കും ഇത് വ്യാപിച്ചു. ഈ രോഗത്തിന്റെ ദ്രുതഗതിയിലുള്ള വർദ്ധനവ് ഫ്ലോറിഡയിൽ മാത്രമല്ല, മുഴുവൻ യുഎസിലും സിട്രസ് വ്യവസായത്തെ ഭീഷണിപ്പെടുത്തി. 2009 ലെ കണക്കനുസരിച്ച്, 33 രാജ്യങ്ങൾ അവരുടെ സിട്രസ് വിളകളിൽ HLB അണുബാധ റിപ്പോർട്ട് ചെയ്തിട്ടുണ്ട്.\n\nഎങ്ങനെ പരിഹരിക്കാം\n\n1. HLB-ബാധിച്ച മരങ്ങൾ പ്ലാസ്റ്റിക് കൂടാരങ്ങളിൽ പൊതിഞ്ഞ് സൂര്യനിൽ ചൂടാക്കുന്നത് സൈലിഡിന്റെ എണ്ണം കുറയ്ക്കുകയോ കുറയ്ക്കുകയോ ചെയ്യുന്നു, ഇത് മരങ്ങളുടെ ഉൽപാദനക്ഷമത വർദ്ധിപ്പിക്കാൻ സാധ്യതയുണ്ട്. നീരാവി ചികിത്സ വൃക്ഷത്തിന്റെ ആയുസ്സ് വർദ്ധിപ്പിക്കുന്നതിനും ഫലം ഉൽപ്പാദിപ്പിക്കുന്നതിനുള്ള കഴിവിനുമുള്ള വാഗ്ദാനവും കാണിക്കുന്നു. ആയിരക്കണക്കിന് ഏക്കർ സിട്രസ് തോട്ടങ്ങളിൽ പച്ചപ്പ് ബാധിച്ചതിനാൽ, ഈ രീതി നടപ്പാക്കൽ വെല്ലുവിളികൾ ഉയർത്തുന്നു. ഗവേഷകർ നിലവിൽ ഇത് വലിയ തോതിൽ ചെയ്യാൻ കഴിയുന്ന വഴികൾ നോക്കുകയാണ്.\n2. സിട്രസ് പച്ചപ്പിന് കാരണമാകുന്ന ബാക്ടീരിയകളെ മന്ദഗതിയിലാക്കാൻ ലക്ഷ്യമിട്ടുള്ള ഒരു പ്രാദേശിക ചികിത്സയാണ് ബാക്ടീരിയനാശിനികൾ. ബാക്ടീരിയ നശിപ്പിക്കുന്നവ മരത്തിലോ പഴങ്ങളിലോ ആഗിരണം ചെയ്യുന്നില്ല. സിട്രസ് മരങ്ങൾക്ക് ഇത് താരതമ്യേന പുതിയ ചികിത്സയാണെങ്കിലും, പിയർ, ആപ്പിൾ കർഷകർ അവരുടെ മരങ്ങളിലെ ബാക്ടീരിയ അണുബാധകളെ ചെറുക്കാൻ വർഷങ്ങളായി ഇത് ഉപയോഗിക്കുന്നു. ദീർഘകാല പരിഹാരങ്ങൾ പര്യവേക്ഷണം ചെയ്യപ്പെടുമ്പോൾ പച്ചപ്പ് നിയന്ത്രിക്കാനും മൊത്തത്തിലുള്ള മരങ്ങളുടെ ആരോഗ്യം വർദ്ധിപ്പിക്കാനും ബാക്ടീരിയനാശിനികൾ സഹായിക്കുമെന്ന് സിട്രസ് കർഷകർ പ്രതീക്ഷിക്കുന്നു.","https://www.floridacitrus.org/newsroom/citrus-411/citrus-greening/potential-citrus-greening-solutions/")');
            print('inserted1: $id11');
            int id12 = await txn.rawInsert(
                'INSERT INTO varietyMal(id,name,details,link) VALUES(17, "പീച്ച് ബാക്ടീരിയൽ സ്പോട്ട്","\tബാക്ടീരിയൽ ഷോട്ട് ഹോൾ എന്നും അറിയപ്പെടുന്ന പീച്ചിന്റെ ബാക്ടീരിയൽ ഇലപ്പുള്ളി, പഴയ പീച്ച് മരങ്ങളിലും നെക്റ്ററൈനുകളിലും ഒരു സാധാരണ രോഗമാണ്. സാന്തോമോനാസ് കാംപെസ്ട്രിസ് പിവി എന്ന ബാക്ടീരിയയാണ് ഈ പീച്ച് ട്രീ ഇലപ്പുള്ളി രോഗത്തിന് കാരണമാകുന്നത്. പ്രൂണി. പീച്ച് മരങ്ങളിലെ ബാക്ടീരിയൽ പാടുകൾ ഫലം നഷ്‌ടപ്പെടുത്തുന്നതിനും ആവർത്തിച്ചുള്ള ഇലപൊഴിയൽ മൂലമുണ്ടാകുന്ന മരങ്ങളുടെ മൊത്തത്തിലുള്ള അസ്വാസ്ഥ്യത്തിനും കാരണമാകുന്നു. കൂടാതെ, ഈ ദുർബലമായ മരങ്ങൾ ശൈത്യകാലത്ത് പരിക്കേൽക്കാനുള്ള സാധ്യത കൂടുതലാണ്.\n\nഎങ്ങനെ പരിഹരിക്കാം\n\n1. രോഗം ബാധിച്ചതോ ചത്തതോ ആയ കൈകാലുകൾ ശരിയായി വെട്ടിമാറ്റി ആവശ്യാനുസരണം വളപ്രയോഗം നടത്തി നിങ്ങളുടെ പീച്ച് മരങ്ങൾ ആരോഗ്യത്തോടെ നിലനിർത്തുക. അമിതമായ നൈട്രജൻ രോഗം വർദ്ധിപ്പിക്കും.\n2. ഈ രോഗനിയന്ത്രണത്തിന് പൂർണ്ണമായും വിജയകരമായ സ്പ്രേകളൊന്നുമില്ലെങ്കിലും, ചെമ്പ് അധിഷ്ഠിത ബാക്ടീരിയനാശിനിയും ഓക്സിടെട്രാസൈക്ലിൻ എന്ന ആൻറിബയോട്ടിക് ഉപയോഗിച്ചുള്ള കെമിക്കൽ സ്പ്രേയും പ്രതിരോധമായി ഉപയോഗിക്കുന്നതിന് ചില ഫലങ്ങളുണ്ട്. വിവരങ്ങൾക്ക് നിങ്ങളുടെ പ്രാദേശിക വിപുലീകരണ ഓഫീസുമായോ നഴ്സറിയുമായോ സംസാരിക്കുക. രാസ നിയന്ത്രണം സംശയാസ്പദമാണ്, എന്നിരുന്നാലും, പ്രതിരോധശേഷിയുള്ള ഇനങ്ങൾ നട്ടുപിടിപ്പിക്കുന്നതാണ് മികച്ച ദീർഘകാല നിയന്ത്രണം.","https://www.gardeningknowhow.com/edible/fruits/peach/bacterial-spot-on-peach-trees.htm")');
