            int id17 = await txn.rawInsert(
                'INSERT INTO varietyMal(id,name,details,link) VALUES(27, "സ്ട്രോബെറി ലീഫ് സ്കോർച്ച്","\tസ്ട്രോബെറി ഇലകൾ കരിഞ്ഞുപോകുന്നതിന് കാരണം സ്ട്രോബെറി നടീലുകളുടെ സസ്യജാലങ്ങളെ ബാധിക്കുന്ന ഒരു ഫംഗസ് അണുബാധയാണ്. ഇതിന് കാരണമാകുന്ന ഫംഗസിനെ ഡിപ്ലോകാർപോൺ എർലിയാന എന്ന് വിളിക്കുന്നു. ഇല പൊള്ളൽ ഉള്ള സ്ട്രോബെറി ആദ്യം ഇലകളുടെ മുകൾഭാഗത്ത് സംഭവിക്കുന്ന ചെറിയ പർപ്പിൾ പാടുകളുടെ വികാസവുമായി ബന്ധപ്പെട്ട പ്രശ്നത്തിന്റെ ലക്ഷണങ്ങൾ കാണിച്ചേക്കാം. കാലക്രമേണ, പാടുകൾ വലുതായി വളരുകയും ഇരുണ്ടതാക്കുകയും ചെയ്യും. കഠിനമായ കേസുകളിൽ, ഇരുണ്ട പാടുകൾ സ്ട്രോബെറി ചെടിയുടെ ഇലകളുടെ മുഴുവൻ ഭാഗങ്ങളും മൂടുകയും അവ പൂർണ്ണമായും ഉണങ്ങുകയും ചെടിയിൽ നിന്ന് വീഴുകയും ചെയ്യും. രോഗം ബാധിച്ച ചെടികളുടെ ഇലകൾ സൗന്ദര്യാത്മകമല്ലെങ്കിലും, ഈ ഫംഗസിന്റെ സാന്നിധ്യം സ്ട്രോബെറി വിളയുടെ ഗുണനിലവാരത്തെ തന്നെ ബാധിക്കുന്നത് അപൂർവമാണ്.\n\nഎങ്ങനെ പരിഹരിക്കാം\n\n1. രോഗബാധിതമായ ചെടികളുടെ കൊഴിഞ്ഞ ഇലകളിൽ ഈ ഫംഗസ് രോഗാണുക്കൾ അതിജീവിക്കുന്നതിനാൽ, ശരിയായ പൂന്തോട്ട ശുചിത്വം പ്രധാനമാണ്. സ്ട്രോബെറി പാച്ചിൽ നിന്ന് രോഗബാധിതമായ പൂന്തോട്ട അവശിഷ്ടങ്ങൾ നീക്കം ചെയ്യുന്നതും പുതിയ സ്ട്രോബെറി ട്രാൻസ്പ്ലാൻറുകൾ പതിവായി സ്ഥാപിക്കുന്നതും ഇതിൽ ഉൾപ്പെടുന്നു. പുതിയ ചെടികളും സ്ട്രോബെറി പാച്ചുകളും സൃഷ്ടിക്കുന്നത് സ്ഥിരമായ സ്ട്രോബെറി വിളവെടുപ്പ് നിലനിർത്തുന്നതിന് പ്രധാനമാണ്, കാരണം പഴയ ചെടികൾ ഗുരുതരമായ അണുബാധയുടെ ലക്ഷണങ്ങൾ കാണിക്കാൻ സാധ്യതയുണ്ട്.","https://www.gardeningknowhow.com/edible/fruits/strawberry/strawberries-with-leaf-scorch.htm")');
            print('inserted1: $id17');
            int id18 = await txn.rawInsert(
                'INSERT INTO varietyMal(id,name,details,link) VALUES(29, "തക്കാളി ബാക്ടീരിയൽ സ്പോട്ട്","\tതക്കാളിയുടെ ബാക്ടീരിയൽ സ്പോട്ട് ഒരു വിനാശകരമായ രോഗമാണ്, അത് കഠിനമായ കേസുകളിൽ, വിപണനം ചെയ്യാനാവാത്ത പഴങ്ങൾക്കും ചെടികളുടെ മരണത്തിനും ഇടയാക്കും. തക്കാളി വളരുന്നിടത്തെല്ലാം ബാക്ടീരിയൽ സ്പോട്ട് ഉണ്ടാകാം, പക്ഷേ ചൂടുള്ളതും ഈർപ്പമുള്ളതുമായ കാലാവസ്ഥയിലും ഹരിതഗൃഹങ്ങളിലും ഇത് പതിവായി കാണപ്പെടുന്നു. വിസ്കോൺസിനിൽ ഈ രോഗം പലപ്പോഴും ഒരു പ്രശ്നമാണ്.\nബാക്റ്റീരിയൽ സ്പോട്ട് എങ്ങനെയിരിക്കും? ഒരു തക്കാളി ചെടിയുടെ ഇലകൾ, കാണ്ഡം, പഴങ്ങൾ എന്നിവയുൾപ്പെടെ മുകളിലെ നിലത്തെ എല്ലാ ഭാഗങ്ങളെയും ബാക്ടീരിയൽ സ്പോട്ട് ബാധിക്കാം. ഇലകളിൽ ചെറിയ (⅛ ഇഞ്ചിൽ താഴെ), ചിലപ്പോൾ വെള്ളത്തിൽ കുതിർന്ന (അതായത്, നനവുള്ള) വൃത്താകൃതിയിലുള്ള ഭാഗങ്ങളിൽ ബാക്ടീരിയൽ സ്പോട്ട് പ്രത്യക്ഷപ്പെടുന്നു. പാടുകൾ തുടക്കത്തിൽ മഞ്ഞ-പച്ചയായിരിക്കാം, പക്ഷേ പ്രായമാകുമ്പോൾ തവിട്ട്-ചുവപ്പ് നിറമാകും. രോഗം മൂർച്ഛിക്കുമ്പോൾ, ഇലകളുടെ വ്യാപകമായ മഞ്ഞനിറം, ഇലകൾ നഷ്ടപ്പെടൽ എന്നിവയും ഉണ്ടാകാം. പച്ചനിറത്തിലുള്ള പഴങ്ങളിൽ, പൊട്ടുകൾ സാധാരണയായി ചെറുതും ഉയർന്നതും കുമിളകൾ പോലെയുള്ളതുമാണ്, കൂടാതെ മഞ്ഞകലർന്ന വലയവും ഉണ്ടായിരിക്കാം. പഴങ്ങൾ പാകമാകുമ്പോൾ, പാടുകൾ വലുതാകുകയും (പരമാവധി ¼ ഇഞ്ച് വലുപ്പത്തിൽ എത്തുകയും ചെയ്യുന്നു) തവിട്ട്, ചൊറി, പരുക്കൻ എന്നിവയായി മാറുന്നു. പ്രായപൂർത്തിയായ പാടുകൾ ഉയർത്തിയേക്കാം, അല്ലെങ്കിൽ ഉയർത്തിയ അരികുകളാൽ മുങ്ങാം. ബാക്ടീരിയൽ സ്‌പെക്ക് എന്ന മറ്റൊരു തക്കാളി രോഗത്തിന്റെ ലക്ഷണങ്ങളുമായി ബാക്ടീരിയൽ സ്പോട്ട് ലക്ഷണങ്ങളെ എളുപ്പത്തിൽ ആശയക്കുഴപ്പത്തിലാക്കാം.\n\nഎങ്ങനെ പരിഹരിക്കാം\n\n1. ബാക്റ്റീരിയൽ സ്പോട്ടുള്ള ഒരു ചെടിയെ സുഖപ്പെടുത്താൻ കഴിയില്ല. ആരോഗ്യമുള്ള ചെടികളിലേക്ക് ബാക്ടീരിയ പടരുന്നത് തടയാൻ രോഗലക്ഷണമുള്ള ചെടികളെ വയലിൽ നിന്നോ ഹരിതഗൃഹത്തിൽ നിന്നോ നീക്കം ചെയ്യുക. രോഗം ബാധിച്ച ചെടികൾ കത്തിക്കുക, കുഴിച്ചിടുക അല്ലെങ്കിൽ ചൂടുള്ള കമ്പോസ്റ്റ് ചെയ്യുക, രോഗലക്ഷണമുള്ള പഴങ്ങൾ കഴിക്കരുത്. ബാക്ടീരിയൽ സ്പോട്ട് രോഗകാരികൾ മനുഷ്യ രോഗകാരികളല്ലെങ്കിലും, അവ ഉണ്ടാക്കുന്ന പഴങ്ങളുടെ പാടുകൾ രോഗത്തിന് കാരണമാകുന്ന മനുഷ്യ രോഗകാരികൾക്ക് പ്രവേശന പോയിന്റുകൾ നൽകും.","https://hort.extension.wisc.edu/articles/bacterial-spot-of-tomato/")');
            print('inserted1: $id18');
            int id19 = await txn.rawInsert(
                'INSERT INTO varietyMal(id,name,details,link) VALUES(30, "ടൊമാറ്റോ ഏർലി ബ്‌ലൈറ്","\tആൾട്ടർനേറിയ ടൊമാറ്റോഫില, ആൾട്ടർനേറിയ സോളാനി എന്നീ രണ്ട് വ്യത്യസ്ത ഫംഗസുകളാൽ ആദ്യകാല ബ്ലൈറ്റ് ഉണ്ടാകാം. ആൾട്ടർനേറിയ ടൊമാറ്റോഫില എ. സോളാനിയെ അപേക്ഷിച്ച് തക്കാളിയിൽ കൂടുതൽ മാരകമാണ്, അതിനാൽ എ. ടൊമാറ്റോഫില കാണപ്പെടുന്ന പ്രദേശങ്ങളിൽ, തക്കാളിയിൽ ആദ്യകാല വരൾച്ചയുടെ പ്രാഥമിക കാരണം ഇതാണ്. എന്നിരുന്നാലും, എ. ടൊമാറ്റോഫില ഇല്ലെങ്കിൽ, എ. സോളാനി തക്കാളിയിൽ ആദ്യകാല വരൾച്ച ഉണ്ടാക്കും.\nരണ്ട് രോഗകാരികളും ഉരുളക്കിഴങ്ങിനെ ബാധിക്കും, എന്നിരുന്നാലും എ. ടൊമാറ്റോഫിലയേക്കാൾ എ. രണ്ട് രോഗകാരികൾക്കും വഴുതനങ്ങയിലും കറുത്ത നൈറ്റ്‌ഷെയ്‌ഡും (Solanum ptycanthum), രോമമുള്ള നൈറ്റ്‌ഷെയ്‌ഡും (Solanum physalifolium) ഉൾപ്പെടെ നിരവധി സോളനേഷ്യസ് കളകളെയും ബാധിക്കാം.\n\nഎങ്ങനെ പരിഹരിക്കാം\n\n1. രോഗകാരികളില്ലാത്ത വിത്ത് ഉപയോഗിക്കുക, അല്ലെങ്കിൽ രോഗബാധയില്ലാത്ത ചെടികളിൽ നിന്ന് മാത്രം വിത്ത് ശേഖരിക്കുക.\n2. കുറഞ്ഞത് രണ്ട് വർഷത്തേക്ക് തക്കാളിയും അനുബന്ധ വിളകളും മാറ്റുക.\n3. കറുത്ത നൈറ്റ്‌ഷെയ്‌ഡും രോമമുള്ള നൈറ്റ്‌ഷെയ്‌ഡും പോലെ വരാൻ സാധ്യതയുള്ള കളകളെ നിയന്ത്രിക്കുക, ഭ്രമണം ചെയ്യുന്ന സമയത്തുടനീളം തക്കാളി ചെടികൾ സ്വമേധയാ വളർത്തുക.\n4. ശക്തമായ സസ്യവളർച്ച നിലനിർത്താൻ ശരിയായി വളപ്രയോഗം നടത്തുക. പ്രത്യേകിച്ച്, പൊട്ടാസ്യം ഉപയോഗിച്ച് അമിതമായി വളപ്രയോഗം നടത്തരുത്, നൈട്രജൻ, ഫോസ്ഫറസ് എന്നിവയുടെ മതിയായ അളവ് നിലനിർത്തുക.\n5. മഴയിലോ ജലസേചനത്തിലോ മഞ്ഞുവീഴ്ചയിലോ ചെടികൾ നനയുമ്പോൾ അവയിൽ ജോലി ചെയ്യുന്നത് ഒഴിവാക്കുക.\n6. ഇലകൾ വരണ്ടതാക്കാൻ ഓവർഹെഡ് ജലസേചനത്തിന് പകരം ഡ്രിപ്പ് ഇറിഗേഷൻ ഉപയോഗിക്കുക.\n7. ചെടികൾക്ക് ചുറ്റുമുള്ള വായുപ്രവാഹം വർദ്ധിപ്പിക്കാനും ഉണങ്ങാൻ സൗകര്യമൊരുക്കാനും ചെടികൾ സ്റ്റേ ചെയ്യുക. സ്റ്റാക്കിംഗ് ഇലകളും ബീജങ്ങളാൽ മലിനമായ മണ്ണും തമ്മിലുള്ള സമ്പർക്കം കുറയ്ക്കും.\n8. ഈർപ്പം കുറയ്ക്കുന്നതിനും മലിനമായ മണ്ണിനും ഇലകൾക്കും ഇടയിൽ തടസ്സം സൃഷ്ടിക്കുന്നതിനും പ്ലാസ്റ്റിക് അല്ലെങ്കിൽ ജൈവ ചവറുകൾ പ്രയോഗിക്കുക","https://extension.umn.edu/diseases/early-blight-tomato")');
